<?php
namespace App\Http\Requests\Event; use Illuminate\Foundation\Http\FormRequest;
class StoreEventRequest extends FormRequest{ public function authorize():bool{ return auth()->check()&&auth()->user()->isAdmin(); } public function rules():array{ return ['title'=>['required','string','max:180'],'description'=>['nullable','string'],'start_date'=>['required','date'],'end_date'=>['nullable','date','after_or_equal:start_date']]; }}